mapper.select:
select *
from student_course
where student_id = #{userId}
  and course_id = #{courseId}

mapper.delete:
delete
from student_course
where student_id = #{userId}
  and course_id = #{courseId}

mapper.select:
select count(*)
from student_course
where course_id = #{courseId}

select count(*)
from student_course su
where su.student_id = #{userId}
